package interval;

import java.util.Objects;

/*
 * This Java source file was generated by the Gradle 'init' task.
 */
public class ClosedInterval {
    private final int lowerEndpoint;
    private int upperEndpoint;

    public ClosedInterval(int lowerEndpoint, int upperEndpoint) {
        this.lowerEndpoint = lowerEndpoint;
        this.upperEndpoint = upperEndpoint;

    }

    public int getLowerEndpoint() {
        return lowerEndpoint;
    }

    public int getUpperEndpoint() {
        return upperEndpoint;
    }

    @Override
    public String toString() {
        return String.format("[%s-%s]", lowerEndpoint, upperEndpoint);
    }

    @Override
    public boolean equals(Object obj) {
        if (obj == null) {
            return false;
        }
        if (getClass() != obj.getClass()) {
            return false;
        }
        ClosedInterval other = (ClosedInterval) obj;
        return lowerEndpoint == other.lowerEndpoint && upperEndpoint == other.upperEndpoint;
    }

    @Override
    public int hashCode() {
        return Objects.hash(lowerEndpoint, upperEndpoint);
    }

    public boolean contains(int value) {
        return lowerEndpoint <= value && value <= upperEndpoint;
    }
}
